import React from 'react';
import Pageadmin from 'layouts/page-admin';
import Select from 'react-select';



class Index extends React.Component{

    constructor(){
        super()
        this.state = {
            options:[],
            value:''
        }
    }

    render(){
        const data  = [
            { id: 1, name: 'Chocolate' },
            { id: 2, name: 'Strawberry' },
            { id: 3, name: 'Vanilla' }
          ];
        
          const data1=[]

          let change =(e)=>{
              this.setState({
                  value: e.value
              })

            let id = document.getElementById('select')
            console.log(id)
          }

          let proses = ()=>{
            data.map( (x , i) =>
                data1.push({
                    value: x.id,
                    label: x.name,
                    nomor: i+1
                })
            )

            this.setState({
                options: data1
            })

            console.log(data1)
          }
        return(
            <Pageadmin>
                
                <div style={{ height: '500px'}}>
                    <h1 className='display-1 text-center'>Midas</h1>
                    <h1 className='display-2 text-center'>Mustikatama Document Archive System</h1>
                </div>
                <Select options={this.state.options} defaultInputValue={this.state.value} onChange={change} id='select' />
                <button onClick={proses}> KLIK DONG </button>
            </Pageadmin>
        )
    }
}


export default Index;
