import React from 'react';
import Baseformmodal from 'layouts/form-modal.jsx';
import { FormGroup , Input } from 'reactstrap';
import { StyledDropZone } from 'react-drop-zone';
import 'react-drop-zone/dist/styles.css';


class formUpload extends React.Component{

    state = {
        files: [],
        kategori: []
      }
    
      addFile = (file, text) => {
        this.setState({ files: [...this.state.files, file] })
        console.log(file)
      }
    render(){
        return(
           <div>
              <Baseformmodal title={'FORM UPLOAD'} captionbtn={'Upload File'}>
                <FormGroup>
                  <Input id="ket" placeholder="Description" type="text" />
                </FormGroup>
                <StyledDropZone onDrop={this.addFile} /><br/>
                <ul>
                  {
                    this.state.files.map(file =>
                      <li key={file.name} >
                        <i className='fa fa-file' /> {file.name}
                      </li>
                    )
                  }
              </ul>
              </Baseformmodal>
           </div>
                
        )
    }
}

export default formUpload;
