import React from "react";
import { BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import FormUpload from './form-repository';
import Pageadmin from 'layouts/page-admin';
import {Button} from 'reactstrap';

class Icons extends React.Component {
  constructor(){
    super()

    this.state = {
      repo: []
    }
  }


  componentDidMount(){
    fetch('http://localhost:4000/repository',{
      method:'get',
      headers: { 'Content-Type':'application/json'}
    })
    .then( res  => res.json())
    .then(data =>{
      this.setState({
        repo: data
      })
    })
  }

  render() {


    const action = ()=>{
      return(
        <Button color='primary' size='sm' type='button'>Download</Button>
      )

    }


    const options = {
      sizePerPage: 10,
      hideSizePerPage: true,
      prePage: 'Back',
      nextPage: 'Next',
    };
    return (
      <Pageadmin head={'List Upload'}>
        <FormUpload/><br/>
        <div className="row">
          <div className="col-md-12">
              <div className="card">
                <div className="content">
                  <BootstrapTable
                    data={this.state.repo}
                    bordered={false}
                    striped
                    pagination={true}
                    options={options}>
                      <TableHeaderColumn
                        dataField='VCIDREPO'
                        isKey = {true}
                        dataSort>
                        ID
                      </TableHeaderColumn>
                      <TableHeaderColumn
                        dataField='DTCREATE'
                        dataSort>
                        Tanggal Dibuat
                      </TableHeaderColumn>
                      <TableHeaderColumn
                        dataField='VCJENISREPO'
                        dataSort>
                        Jenis Laporan
                      </TableHeaderColumn>
                      <TableHeaderColumn
                        dataField='VCSTANDARTFILENAME'
                        dataSort>
                        Jenis Laporan
                      </TableHeaderColumn>
                      <TableHeaderColumn
                        dataFormat={action}>
                        Action
                      </TableHeaderColumn>
                  </BootstrapTable>
                </div>
              </div>
            </div>
          </div>  
      </Pageadmin>
    );
  }
}

export default Icons;
