import React, { Component } from 'react';
import Pageadmin from 'layouts/page-admin';
import app from 'app';
import Select from 'react-select';
import { Row , Col } from 'reactstrap';
import Tabel from 'layouts/tabel';

export default class listUsers extends Component {
    constructor(){
        super()
        this.state = {
            sbu:[],
            dpt:[],
            modal: false,
            users: []
        }

        this.mode = this.mode.bind(this);
        this.dpt = this.dpt.bind(this);
    }
    componentWillMount(){
        app.apiGet('sbu')
        .then(res =>{
            this.setState({
                sbu: res
            })
        })
    }

    mode(){
        this.setState({ modal: !this.state.modal})
    }

    dpt(e){
        app.apiGet1('dpt',e.value)
           .then(res =>{
               this.setState({
                   dpt: res
               })
           })  
    }


    render() {
        return (
            <div>
                <Pageadmin>
                    <Row>
                        <Col sm='3'>
                            <Select placeholder={'Pilih SBU'} onChange={this.dpt} options={this.state.sbu.map((x)=>({
                                            value: x.SYSTEM_ID,
                                            label: x.SBU
                                        }))} />
                        </Col>
                        <Col sm='3'>
                            <Select placeholder={'Pilih Division'} options={this.state.dpt.map((x)=>({
                                            value: x.SYSTEM_ID,
                                            label: x.DIVISION
                                        }))} />
                        </Col>
                    </Row>
                    <Row>
                        <Col sm='12'>
                            <Tabel>
                                
                            </Tabel>
                        </Col>
                    </Row>
                </Pageadmin>
            </div>
        )
    }
}
