import React from 'react';
import Baseformmodal from 'layouts/form-modal.jsx';
import { FormGroup ,Input ,Label } from 'reactstrap';
import 'react-drop-zone/dist/styles.css';


class formUpload extends React.Component{

    state = {
        files: []
      }
    
      addFile = (file, text) => {
        this.setState({ files: [...this.state.files, file] })
        console.log(file)
      }
    render(){
        return(
           <div>
               <Baseformmodal title={'FORM KATEGORI'} captionbtn={'Tambah Kategori'}>
               <FormGroup>
                  <Label>Nama Master Report</Label>
                  <Input placeholder="Master Report" type="text"/>
               </FormGroup>
               <FormGroup>
                  <Label>Standart File Name</Label>
                  <Input placeholder="File Name" type="text"/>
               </FormGroup>
               <FormGroup>
                  <Label>Jenis Report</Label>
                  <Input type="select">
                    <option value="">Pilih Jenis Report</option>
                    <option value="Harian">Harian</option>
                    <option value="Mingguan">Mingguan</option>
                    <option value="Bulanan">Bulanan</option>
                    <option value="Tahunan">Tahunan</option>
                  </Input>
               </FormGroup>
                <FormGroup>
                  <Label>Keterangan</Label>
                  <Input placeholder="Keterangan" type="textarea"/>
               </FormGroup>
              </Baseformmodal>
           </div>
                
        )
    }
}

export default formUpload;
