import React from 'react';
import {
    Button,
    Modal
} from 'reactstrap';

class formModal extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            modal: false
        }
    }

    mode = () =>{
        this.setState({
            modal: !this.state.modal
        })
    }

    render(){
        return (
            <React.Fragment><br/><br/><br/><br/><br/>
            <Button color="primary" onClick={this.mode}> Show Modal </Button>  
            {console.log(this.state)}        
            <Modal
                className = 'modal-dialog-centered'
                isOpen = {this.state.modal}
                toggle = {this.modal}
            >
                <div className='modal-header'>
                    <h5 className='modal-title' id='exampleModalLabel'>
                        percobaan
                    </h5>
                    <Button
                        aria-label='Close'
                        className='close'
                        data-dismiss ='modal'
                        type ='button'
                        onClick={this.mode}
                    >
                        <span aria-hidden = {true}> x </span>
                    </Button>
                    <div className='modal-body'>
                        halooo
                    </div>
                    <div className='modal-footer'>
                        <Button color='primary' type='button'>Simpan</Button>
                        <Button color='secondary' data-dismiss='modal' type='button' onClick={this.mode}>Close</Button>
                    </div>
                </div>
            </Modal>
            </React.Fragment>
        )
    }
}

export default formModal;
