import React from 'react';
import { BootstrapTable , TableHeaderColumn } from 'react-bootstrap-table';
import Pageadmin from 'layouts/page-admin';
import { Input ,Row , Col , FormGroup , Button } from 'reactstrap';
import app from 'app';


class listAccessmenu extends React.Component{
constructor(){
    super()
    this.state = {
        sbu: [],
        dpt: [],
        user:[],
        data:[]
    }
}


componentDidMount(){
    app.apiGet('sbu')
       .then(res =>{
           this.setState({
               sbu: res
           })
       })
}

dpt = (e)=>{
    app.apiGet1('dpt',e.target.value)
       .then(res =>{
           this.setState({
               dpt: res
           })
       })  
}

user = (e) =>{
    let sbu = document.getElementById('sbu').value;

    app.apiGet2('userFilter',sbu , e.target.value)
       .then(res =>{
           this.setState({
               user: res
           })
       })
}

data = (e) =>{
  app.apiGet1('accessrepo',e.target.value)
     .then(res =>{
         this.setState({
             data: res
         })
     })  

     console.log(this.state.data)
}


render(){
    return(
        <Pageadmin head={'List Access Repository'}>
            <Row style={{marginBottom:'20px'}}>
                <Col md='3'>
                    <FormGroup>
                        <Input type='select' onChange={this.dpt} id="sbu">
                        <option value="0">Pilih SBU</option>
                            {
                                this.state.sbu.map(data =>
                                 <option key={data.SYSTEM_ID} value={data.SYSTEM_ID}>{data.SBU}</option>   
                                )
                            }
                        </Input>
                    </FormGroup>
                </Col>
                <Col md='3'>
                    <FormGroup>
                        <Input type='select' onChange={this.user}>
                        <option value="0">Pilih Division</option>
                        {
                            this.state.dpt.map(data =>
                                <option key={data.SYSTEM_ID} value={data.SYSTEM_ID}>{data.DIVISION}</option>    
                            )
                        }
                        </Input>
                    </FormGroup>
                </Col>
                <Col md='3'>
                    <FormGroup>
                        <Input type='select'>
                        <option value="0" onChange={this.data}>Pilih User</option>
                        {
                            this.state.user.map(data =>
                                <option key={data.IDLOGIN} value={data.IDLOGIN}>{data.USERNAME}</option>    
                            )
                        }
                        </Input>                      
                    </FormGroup>
                </Col>
            </Row>  
            <BootstrapTable
                bordered={false}
                striped
                pagination={true}
                options= {app.optionTable}
                >
                <TableHeaderColumn
                    dataField='DTPERIOD'
                    width='16%'
                    isKey = {true}
                    dataSort>
                    Periode
                </TableHeaderColumn>
                <TableHeaderColumn
                    dataField='KATEGORI'
                    width='16%'
                    dataSort>
                    Kategori
                </TableHeaderColumn>
                <TableHeaderColumn
                    dataField='COUNTFILE'
                    width='16%'
                    dataSort>
                    Jumlah File
                </TableHeaderColumn>
                <TableHeaderColumn
                    dataField="button"
                
                    width='16%'
                    dataSort>
                    Action
                </TableHeaderColumn>              
            </BootstrapTable>
        </Pageadmin>
    )
}

}

export default listAccessmenu;
