import React, { Component } from 'react';
import Page from 'layouts/page-admin';
import Autosuggest from 'react-autosuggest';
import './src.css';
import { Row , 
         Col , 
         Button,
         ListGroupItem,
         ListGroup
        } from 'reactstrap';
import app from 'app';


export default class SearchReport  extends Component {
    constructor() {
        super();
    
        this.state = {
          value: '',
          suggestions: [],
          laporan: [],
          result: []
        };
      }

      componentWillMount(){
        let user = app.dataUser[0].ACCESS;
        let dpt = app.dataUser[0].IDDPT || 0;

        app.apiGet2('search/repo',user , dpt)
           .then(res=>{
               
           })
      }

    
      getSuggestions = value => {
        let { laporan } = this.state;
        const inputValue = value.trim().toLowerCase();
        const inputLength = inputValue.length;
      
        return inputLength === 0 ? [] : laporan.filter(x =>
          x.REPO.toLowerCase().slice(0, inputLength) === inputValue
        );

      };
      
    
      getSuggestionValue = suggestion => suggestion.REPO;
    
      renderSuggestion = suggestion => (
        <div>
          {suggestion.REPO}
        </div>
      );
    
      onChange = (event, { newValue }) => {
        this.setState({
          value: newValue
        });
      };
    
      onSuggestionsFetchRequested = ({ value }) => {
        this.setState({
          suggestions: this.getSuggestions(value)
        });

      };
    
      onSuggestionsClearRequested = () => {
        this.setState({
          suggestions: []
        });
      };

      Search =()=>{
          let { value , laporan} = this.state;

          let hasil = laporan.filter( x => x.REPO.toLowerCase().includes(value.toLocaleLowerCase()));

          this.setState({ result: hasil})
      }
    
    render() {

        const { value, suggestions , result } = this.state;

        const inputProps = {
        placeholder: 'Cari Laporan Anda',
        value,
        onChange: this.onChange
        };
          
        return (
            <Page head={'Pencarian Laporan'}>
                <Row className='mb-3'>
                    <Col md='12'>
                    <Autosuggest 
                        suggestions={suggestions}
                        onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
                        onSuggestionsClearRequested={this.onSuggestionsClearRequested}
                        getSuggestionValue={this.getSuggestionValue}
                        renderSuggestion={this.renderSuggestion}
                        inputProps={inputProps}
                    />
                    </Col>
                </Row> 
                <Button color='primary' onClick={this.Search}> Search </Button>
                <Button color='info'> Filter </Button>
                <hr />
                {  
                    result.length === 0 ? <div><h1> Data Tidak Ada </h1></div> :
                    result.map(x =>(
                        <ListGroup flush key={x.ID_FILE}>
                            <ListGroupItem
                                className="list-group-item-action flex-column align-items-start py-4 px-4"
                                onClick={e => e.preventDefault()}
                            >
                                <div className="d-flex w-100 justify-content-between">
                                <div>
                                    <div className="d-flex w-100 align-items-center">
                                    <Button size='sm' color='success'> Download </Button>
                                    </div>
                                </div>
                                <small>{`${x.DIVISION} ${x.DTUPLOAD}`}</small>
                                </div>
                                <h4 className="mt-3 mb-1">{x.REPO}</h4>
                                <p className="text-sm mb-0">
                                {`${x.JENIS_REPO} ${x.TEMPLATE_NAME}`}
                                </p>
                            </ListGroupItem>
                        </ListGroup>
                    ))
                }
            </Page>
        )
    }
}
