import React from 'react';
import Pageadmin from 'layouts/page-admin';
import { BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import Scroll from 'simplebar-react';
import { Row , Col , CardTitle , Button , Input} from 'reactstrap';
import app from 'app';


class ListLaporanAnalisa extends React.Component{
    constructor(){
        super()
        this.state={
            accperiod:[],
            idperiod: app.dataUser[0].ID_PERIOD
        }
    }


    componentWillMount(){
        app.apiGet('accperiod')
           .then(res =>{
             this.setState({
               accperiod: res
             })
           })
    }
    render(){
        let periode = [
            {
                name: 'Laporan Harian'
            },
            {
                name: 'Laporan Mingguan'
            },
            {
                name: 'Laporan Bulanan'
            },
            {
                name: 'Laporan Tahunan'
            },
            {
                name: 'Laporan Insidentil'
            }
        ]
        return(
            <Pageadmin head={'Laporan Analisa'}>
                <Input type="select" id="period" style={{marginBottom: '10px' , width:'23%'}}  onChange={this.selectPeriod} value={this.state.idperiod}>
                {
                    this.state.accperiod.map(data =>
                    <option key={data.VCIDACCPERIOD} value ={data.VCIDACCPERIOD}>{data.VCDESCRIPTION}</option> 
                    )
                }
                </Input>
                <Row style={{ height: '450px'}}>
                    <Col sm='3'>
                    {
                        periode.map( data =>
                            <Button color="default" outline type='button' style={{ width: '100%'}}>
                                <Row>
                                    <div className="col">
                                        <CardTitle className="font-weight-bold" style={{marginTop:'10px'}}>
                                            {data.name}
                                        </CardTitle>
                                    </div>
                                    <Col className="col-auto">
                                        <div className="icon icon-shape bg-danger text-white rounded-circle shadow">
                                            <i className="fas fa-chart-bar" />
                                        </div>
                                    </Col>
                                </Row>
                            </Button> 
                        )
                    }
                    </Col>
                    <Col sm='9'>
                        <Scroll>
                            <BootstrapTable
                                data={periode}
                                bordered={false}
                                striped
                                pagination={true}
                                options={app.optionTable}>
                                <TableHeaderColumn
                                    dataField='TEMPLATE'
                                    width='16%'
                                    isKey = {true}
                                    dataSort>
                                    Nama Laporan
                                </TableHeaderColumn>
                                <TableHeaderColumn
                                    dataField='LAST_UPLOAD'
                                    width='16%'
                                    dataSort>
                                    Last Upload
                                </TableHeaderColumn>
                                <TableHeaderColumn
                                    dataField='COUNT'
                                    width='16%'
                                    dataSort>
                                    Sudah Upload
                                </TableHeaderColumn>
                                <TableHeaderColumn
                                    dataField='COUNT'
                                    dataFormat={this.status}
                                    width='16%'
                                    dataSort>
                                    Belum Upload
                                </TableHeaderColumn>      
                                <TableHeaderColumn
                                    dataField='ID_TEMPLATE'
                                    dataFormat={this.action}
                                    width='16%'
                                    dataSort>
                                    Persen
                                </TableHeaderColumn>
                            </BootstrapTable>
                        </Scroll>
                    </Col>
                </Row>
            </Pageadmin>
        )
    }
}


export default ListLaporanAnalisa;
