import React, { Component } from 'react';
import Page from 'layouts/page-admin';
import Autosuggest from 'react-autosuggest';
import './src.css';
import { Row , 
         Col , 
         Button 
        } from 'reactstrap';
import app from 'app';


export default class SearchReport  extends Component {
    constructor() {
        super();
    
        this.state = {
          value: '',
          suggestions: [],
          laporan: {}
        };
      }

      componentWillMount(){
        app.apiGet('search')
           .then(res=>{
               let user = app.dataUser[0].ACCESS;

               if (user === 2) {
                    this.setState({
                        laporan: res
                    })
               }else{
                   this.setState({
                       laporan: res
                   })
               }
           })
      }

    
      getSuggestions = value => {
        let { laporan } = this.state;
        const inputValue = value.trim().toLowerCase();
        const inputLength = inputValue.length;
      
        return inputLength === 0 ? [] : laporan.filter(x =>
          x.REPO.toLowerCase().slice(0, inputLength) === inputValue
        );

      };
      
    
      getSuggestionValue = suggestion => suggestion.name;
    
      renderSuggestion = suggestion => (
        <div>
          {suggestion.name}
        </div>
      );
    
      onChange = (event, { newValue }) => {
        this.setState({
          value: newValue
        });
      };
    
      onSuggestionsFetchRequested = ({ value }) => {
        this.setState({
          suggestions: this.getSuggestions(value)
        });

        console.log(this.state.suggestions)
      };
    
      onSuggestionsClearRequested = () => {
        this.setState({
          suggestions: []
        });
      };
    
    render() {

        const { value, suggestions } = this.state;

        const inputProps = {
        placeholder: 'Cari Laporan Anda',
        value,
        onChange: this.onChange
        };
          
        return (
            <Page head={'Pencarian Laporan'}>
                <Row className='mb-3'>
                    <Col md='12'>
                    <Autosuggest 
                        suggestions={suggestions}
                        onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
                        onSuggestionsClearRequested={this.onSuggestionsClearRequested}
                        getSuggestionValue={this.getSuggestionValue}
                        renderSuggestion={this.renderSuggestion}
                        inputProps={inputProps}
                    />
                    </Col>
                </Row> 
                <Button color='primary'> Search </Button>
                <Button color='info'> Filter </Button>
                <hr />
            </Page>
        )
    }
}
