import React from 'react';
import { BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import Pageadmin from 'layouts/page-admin';
import Formfile from './form-file';
import { Button , Row , Col , Card ,CardTitle ,CardText } from 'reactstrap';

class listFile extends React.Component{
    constructor(){
        super()
    
        this.state = {
          file: []
        }
      }
    
    
      componentDidMount(){
        fetch('http://localhost:4000/uploadfile',{
          method:'get',
          headers: { 'Content-Type':'application/json'}
        })
        .then( res  => res.json())
        .then(data =>{
          this.setState({
            file: data
          })
        })
      }
    
      render() {

       let action = ()=>{
          return(
            <Button type="button" size="sm" color="success">Download</Button>
          )
          
        }
     
        const options = {
          sizePerPage: 10,
          hideSizePerPage: true,
          prePage: 'Back',
          nextPage: 'Next',
        };
        return (
          <Pageadmin head={'List File'}>
          <Formfile /><br/>
          <Row>
              <Col sm="6">
                <Card body>
                  <CardTitle>Special Title Treatment</CardTitle>
                  <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                  <Button>Go somewhere</Button>
                </Card>
              </Col>
              <Col sm="6">
                <Card body>
                  <CardTitle>Special Title Treatment</CardTitle>
                  <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                  <Button>Go somewhere</Button>
                </Card>
              </Col>
            </Row>
          </Pageadmin>
        );
      }
    }

export default listFile;
