import React ,  { Component } from 'react';
import app from 'app';
import { Link } from "react-router-dom";

export default function withAuth(View) {
    
    return class Auth extends Component{
       
        render(){
            if (app.loggedin()) {
                return <View />
            }else{
                return window.location.href ='/login'
            }

            
        }
    }
}
