import React from 'react';
import { BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import Pageadmin from 'layouts/page-admin';
import Formfile from './form-file';
import { Button } from 'reactstrap';

class listFile extends React.Component{
    constructor(){
        super()
    
        this.state = {
          file: []
        }
      }
    
    
      componentDidMount(){
        fetch('http://localhost:4000/uploadfile',{
          method:'get',
          headers: { 'Content-Type':'application/json'}
        })
        .then( res  => res.json())
        .then(data =>{
          this.setState({
            file: data
          })
        })
      }
    
      render() {

        let action = ()=>{
          <Button>Download</Button>
        }
     
        const options = {
          sizePerPage: 10,
          hideSizePerPage: true,
          prePage: 'Back',
          nextPage: 'Next',
        };
        return (
          <Pageadmin head={'List File'}>
          <Formfile /><br/>
            <div className="row">
              <div className="col-md-12">
                  <div className="card">
                    <div className="content">
                      <BootstrapTable
                        data={this.state.file}
                        bordered={false}
                        striped
                        pagination={true}
                        options={options}>
                          <TableHeaderColumn
                            dataField='DTUPLOAD'
                            width='20%'
                            dataSort>
                            Tanggal Upload
                          </TableHeaderColumn>
                           <TableHeaderColumn
                            dataField='VCDESCRIPTION'
                            width='20%'
                            dataSort>
                            Description
                          </TableHeaderColumn>
                          <TableHeaderColumn
                            dataField='REPO'
                            width='20%'
                            dataSort>
                            Kategori Laporan
                          </TableHeaderColumn>
                          <TableHeaderColumn
                            dataField='VCFILENAME'
                            width='20%'
                            isKey = {true}
                            dataSort>
                            File Name
                          </TableHeaderColumn>
                          <TableHeaderColumn
                            dataField='VCORIGINALNAME'
                            width='20%'
                            dataSort>
                            Original Name
                          </TableHeaderColumn>
                          <TableHeaderColumn
                            dataField="button"
                            dataFormat={this.action}
                            width='20%'
                            dataSort>
                            Action
                          </TableHeaderColumn>
                      </BootstrapTable>
                    </div>
                  </div>
                </div>
              </div>  
          </Pageadmin>
        );
      }
    }

export default listFile;
