import React from 'react';
import { BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import Pageadmin from 'layouts/page-admin';
import Formfile from './form-file';
import { Button , Row , Col , Card ,CardTitle} from 'reactstrap';

class listFile extends React.Component{
    constructor(){
        super()
    
        this.state = {
          repo: [],
          file: [] 
        }
      }
    
    
      componentDidMount(){
        fetch('http://localhost:4000/repository',{
          method:'get',
          headers: { 'Content-Type':'application/json'}
        })
        .then( res  => res.json())
        .then(data =>{
          this.setState({
            repo: data
          })
        })
      }
    
      render() {

       let action = ()=>{
          return(
            <Button type="button" size="sm" color="success">Download</Button>
          )
          
        }
     
        const options = {
          sizePerPage: 10,
          hideSizePerPage: true,
          prePage: 'Back',
          nextPage: 'Next',
        };
        return (
          <Pageadmin head={'List File'}>
          <Formfile /><br/>
          <Row>
              <Col sm="3">
              {
                this.state.repo.map(repo =>
                  <Card body key={repo.vcidrepo}>
                    <CardTitle>{repo.VCDESCRIPTION}</CardTitle>
                    <Button size="sm" color="info" outline>List File</Button>
                  </Card>
                )
              }
              </Col>
              <Col sm="9">
              <div className="row">
              <div className="col-md-12">
                  <div className="card">
                    <div className="content">
                      <BootstrapTable
                        data={this.state.file}
                        bordered={false}
                        striped
                        pagination={true}
                        options={options}>
                          <TableHeaderColumn
                            dataField='DTUPLOAD'
                            width='16%'
                            dataSort>
                            Tanggal Upload
                          </TableHeaderColumn>
                           <TableHeaderColumn
                            dataField='VCDESCRIPTION'
                            width='16%'
                            dataSort>
                            Description
                          </TableHeaderColumn>
                          <TableHeaderColumn
                            dataField='REPO'
                            width='16%'
                            dataSort>
                            Kategori Laporan
                          </TableHeaderColumn>
                          <TableHeaderColumn
                            dataField='VCFILENAME'
                            width='16%'
                            isKey = {true}
                            dataSort>
                            File Name
                          </TableHeaderColumn>
                          <TableHeaderColumn
                            dataField="button"
                            dataFormat={action}
                            width='16%'
                            dataSort>
                            Action
                          </TableHeaderColumn>
                      </BootstrapTable>
                    </div>
                  </div>
                </div>
              </div>  
              </Col>
            </Row>
          </Pageadmin>
        );
      }
    }

export default listFile;
