import React from 'react';
import { BootstrapTable , TableHeaderColumn } from 'react-bootstrap-table';
import Pageadmin from 'layouts/page-admin';
import { Input ,Row , Col , FormGroup , Button } from 'reactstrap';
import app from 'app';


class listAccessmenu extends React.Component{
constructor(){
    super()
    this.state = {
        sbu: [],
        dpt: [],
        user:[]
    }
}


componentDidMount(){
    app.apiGet('sbu')
       .then(res =>{
           this.setState({
               sbu: res
           })
       })
}

dpt = (e)=>{
    app.apiGet1('dpt',e.target.value)
       .then(res =>{
           this.setState({
               dpt: res
           })
       })  
}



render(){
    return(
        <Pageadmin head={'List Access Repository'}>
            <Row style={{marginBottom:'20px'}}>
                <Col md='3'>
                    <FormGroup>
                        <Input type='select'/>
                        <option value="0">Pilih SBU</option>
                    </FormGroup>
                </Col>
                <Col md='3'>
                    <FormGroup>
                        <Input type='text'/>
                    </FormGroup>
                </Col>
                <Col md='3'>
                    <FormGroup>
                        <Input type='text'/>
                    </FormGroup>
                </Col>
                <Button type="button" color="default">Cari Data</Button>
            </Row>  
            <BootstrapTable
                bordered={false}
                striped
                pagination={true}
                options= {app.optionTable}
                >
                <TableHeaderColumn
                    dataField='DTPERIOD'
                    width='16%'
                    isKey = {true}
                    dataSort>
                    Periode
                </TableHeaderColumn>
                <TableHeaderColumn
                    dataField='KATEGORI'
                    width='16%'
                    dataSort>
                    Kategori
                </TableHeaderColumn>
                <TableHeaderColumn
                    dataField='COUNTFILE'
                    width='16%'
                    dataSort>
                    Jumlah File
                </TableHeaderColumn>
                <TableHeaderColumn
                    dataField="button"
                
                    width='16%'
                    dataSort>
                    Action
                </TableHeaderColumn>              
            </BootstrapTable>
        </Pageadmin>
    )
}

}

export default listAccessmenu;
