import React ,  { Component } from 'react';
import app from 'app';

export default function withAuth(View) {
    
    return class Auth extends Component{
        constructor(){
            super()
            this.state = {
                user: null
            }
        }

        componentWillMount(){
            if (!app.loggedin()) {
                this.props.history.replace('/login');
            }else{
                try{
                    let profile = Aapp.getProfile();
                    this.setState({
                        user: profile
                    })
                }
                catch(err){
                    app.logout();
                    this.props.history.replace('/login');

                }
            }
        }

        render(){
            if (this.state.user) {
                return 'Haloo'
            }else{
                return null
            }
        }
    }
}
