import React from 'react';
import Pageadmin from 'layouts/page-admin';
import { Row , Col , Card , CardHeader , CardText}  from 'reactstrap'; 
import app from 'app.js';


class Formatfile extends React.Component{
    constructor(){
        super()
        this.state = {
            laporan: []
        }
    }

    componentWillMount(){
        let id = JSON.parse(localStorage.getItem('user'))[0].IDLOGIN;    
        app.apiGet1('formatfile' , id )
           .then(res =>{
               this.setState({
                   laporan: res
               })
           })
    }

    render(){
        return(
            <Pageadmin head={'Format File'}>
                <Row>
                    <Col sm='12'>
                        {
                            this.state.laporan.map(repo =>
                                <Card key={repo.IDACC_REPO}>
                                    <CardHeader>{repo.REPOSITORY}</CardHeader>
                                    <CardText>{repo.KET}</CardText>
                                </Card>    
                            )
                        }
                    </Col>
                </Row>
            </Pageadmin>
        )
    }
}

export default Formatfile;
