import React, { Component } from 'react';
import Page from 'layouts/page-admin';
import {
    Row,
    Col,
    ListGroupItem,
    ListGroup,
    Badge,
    Input,
    Navbar,
    Button
 
} from 'reactstrap';
import app from 'app';
import Scroll from 'simplebar-react';
import socket from 'socket.io-client';
const io  = socket('http://192.168.40.100:4000');

export default class ListChats extends Component {
    constructor(){
        super()
        this.state = {
            users: [],
            filter: '',
            chats : [{
                        from:'orang',
                        msg: 'Selamat Pagi'
                    },
                    {
                        from:'aku',
                        msg: 'Selamat Pagi Juga'
                    },
                    {
                        from:'orang',
                        msg: 'Perkenalkan Aku Andi'
                    },
                    {
                        from:'aku',
                        msg: 'Salam Kenal Juga'
                    }
                ],
            active:false,
            name:'',
            msg: ''
        }
        this.getFilter = this.getFilter.bind(this);
        this.setName = this.setName.bind(this);
        this.setMsg = this.setMsg.bind(this);
        this.SendMsg = this.SendMsg.bind(this);
    }



    componentWillMount(){
        app.apiGet('chats/users')
            .then(res =>{
                this.setState({ users: res})
            })
    }

    getFilter( e ){
        this.setState({ filter: e.target.value })
    }

    setName(name){
        this.setState({ 
            name : name,
            active: true
        })
    }

    setMsg(e){
        this.setState({ msg: e.target.value})
    }

    SendMsg(){
        let { msg } = this.state;
        let data = { 
            from: 'aku',
            msg: msg
         }
        io.emit('chat' , data );
    }

    render() {
        let { users , filter , chats , active , name } = this.state;

        let dataUsers = users.filter( x =>{
            return x.USERNAME.toLowerCase().includes(filter.toLocaleLowerCase())
        });

        io.on('chat' , (msg)=>{
            //this.setState({ ...this.state.chats , chats: [msg]})
            let data = [ ...this.state.chats , msg]

            console.log(data)
        })

        return (
            <Page head={'Chats MTG'}>
                <Row>
                    <Col md='3'>
                        <Input type='text' className='mb-2' onChange={this.getFilter} placeholder={'Cari User'} />
                        <Scroll style={{ height: '500px' }}>
                            {   
                                dataUsers.map(x => (
                                    <ListGroup flush key={x.IDLOGIN} >
                                        <ListGroupItem
                                            className="list-group-item-action flex-column align-items-start py-4 px-4"
                                            href="#pablo"
                                            onClick={()=> this.setName(x.USERNAME)}
                                            tag="a"
                                            color='default'
                                        
                                        >
                                            <div className="d-flex w-100 justify-content-between">
                                            <div>
                                                <div className="">
                                                <h5  className="mb-1">{x.USERNAME}</h5>
                                                </div>
                                            </div>
                                                <small className='text-center'>
                                                    <Badge className="badge-success">Online</Badge><br/>
                                                    <h4>{x.DIVISION}</h4>
                                                </small>
                                            </div>
                                        </ListGroupItem>
                                    </ListGroup>
                                ))
                            }
                        </Scroll>
                    </Col>
                    <Col md='9'>
                        {
                            active ? <div>
                                        <Navbar className="navbar-horizontal bg-primary ">
                                        <h3 className='text-secondary'>{name}</h3>
                                        </Navbar>
                                        <hr />
                                        <Scroll style={{ height: '400px' , }}>
                                        <ul>
                                            {
                                                chats.map((x , i) =>(
                                                    x.from === 'aku' ?
                                                    <Row className="d-flex flex-row-reverse" key={i}>
                                                        <Col md='6'> 
                                                            <ListGroup flush  >
                                                                <ListGroupItem color='success text-dark' style={{ borderRadius: '20px' , fontSize:'13px'}}>
                                                                    {x.msg}
                                                                </ListGroupItem>
                                                            </ListGroup>
                                                        </Col>
                                                    </Row>
                                                    :
                                                    <Row className="d-flex flex-row" key={i}>
                                                        <Col md='6'> 
                                                            <ListGroup flush  >
                                                                <ListGroupItem color='success text-dark' style={{ borderRadius: '20px' , fontSize:'13px' }}>
                                                                   {x.msg}
                                                                </ListGroupItem>
                                                            </ListGroup>
                                                        </Col>
                                                    </Row>
                                                ))
                                            }
                                            </ul>
                                        </Scroll>
                                        <Row>
                                            <Col md='10'>
                                                <Input type='text' onChange={this.setMsg}/>
                                            </Col>
                                            <Col md='2'>
                                                <Button color='success' onClick={this.SendMsg}>Kirim</Button>
                                            </Col>
                                        </Row>
                                    </div> :
                            <h1 className='text-center'>Chat MTG</h1> 
                        }
                        
                    </Col>
                </Row>
            </Page>
        )
    }
}
