import React from 'react';
import {
    Button,
    Modal
} from 'reactstrap';
import 'app.css';
import app from 'app';

class formModal extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            modal: false
        }
    }

    action = () =>{
        app.store.dispatch({ type: 'CLOSE_MODAL'})
    }

    render(){
        return (
            <React.Fragment>  
            <Modal
                className = 'modal-dialog-centered v_lg'
                isOpen = {app.store.getState().modal}
                //toggle = {app.store.dispatch({type: 'MODAL'})}
                size ='lg'
            >
                <div className='modal-header'>
                    <h5 className='modal-title' id='exampleModalLabel'>
                        {this.props.title}
                    </h5>
                    <Button
                        aria-label='Close'
                        className='close'
                        data-dismiss ='modal'
                        type ='button'
                        onClick={ this.action}
                    >
                        <span aria-hidden = {true}> x </span>
                    </Button>
                </div>
                <div className='modal-body'>
                    {this.props.children}
                </div>
                <div className='modal-footer'>

                </div>
            </Modal>
            </React.Fragment>
        )
    }
}

export default formModal;
