import sw from 'sweetalert2';
import Decode from 'jwt-decode';


let encode = (file)=>{

var reader = new FileReader();
reader.onloadend = ()=>{
    return reader.result;
}

return reader.readAsDataURL(file)

    
}

/* API */

    let proxy = 'http://192.168.40.88:4000/';
    let headers = {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Authorization': 'Bearer'+ localStorage.getItem('token')
    }

    let apiGet = (url)=>{
        return fetch(proxy+url , {
            method: 'get',
            headers
        })
        .then(res => res.json())
        .then(data =>{
            return data
        })
    }

    let apiGet1 = (url , params1)=>{
        return fetch(proxy+url+'/'+params1 , {
            method: 'get'
        })
        .then(res => res.json())
        .then(data =>{
            return data
        })
    }

    let apiGet2 = (url , params1 , params2)=>{
        return fetch(proxy+url+'/'+params1+'/'+params2 , {
            method: 'get'
        })
        .then(res => res.json())
        .then(data =>{
            return data
        })
    }


    let apiPostFormdata = (url , data)=>{
        return fetch(proxy+url,{
            method: 'post',
            body: data
        })
        .then(res => res.json())
        .then(data =>{
            return data;
        })
    }


    let apiPostJson = (url , data)=>{
        return fetch(proxy+url,{
            method: 'post',
            headers: { 'Content-Type':'application/json'},
            body: JSON.stringify(data)
        })
        .then(res => res.json())
        .then(data =>{
            return data;
        })
    }
/* API */

/* SWET ALERT */
    let msgerror = (msg) =>{
        sw.fire({
        type: 'error',
        title: 'Oops...',
        text: msg
        })
    }

    let msgok =(msg , url)=>{
        sw.fire({
            type: "success",
            title: "Berhasil",
            text: msg  
        }).then(()=>{
            window.location.href = url
        })
    }
/* SWET ALERT */

/* Auth */

    let login = (user , pass)=>{
        let data = {
            name: user,
            pass: pass
        }
        return apiPostJson('/login', data)
                .then(res =>{
                    localStorage.setItem('token' , res.token)
                })
    }

    let loggedin = ()=>{
        let token = localStorage.getItem('token');
        return !!token && !isTokenExpired(token)
    

    }

    let isTokenExpired = (token)=>{
        try{
            let decode = Decode(token);

            if (decode.exp < Date.now() / 1000){
                return true
            }else{
                return false
            }
        }

        catch(err){
            return false;
        }
    }

    let getProfile = ()=>{
        return Decode(localStorage.getItem('token'));
    }

    let logout = ()=>{
        localStorage.removeItem('token')
    }


/* Auth */







export default { encode , proxy , apiGet , apiGet1 , apiGet2 ,apiPostJson ,apiPostFormdata , msgerror , msgok ,login , loggedin ,getProfile ,logout}
