import React, { Component } from 'react';
import Modal from 'layouts/list_modal';
import Scroll from 'simplebar-react';
import { ChatItem } from 'react-chat-elements';
import { 
    Button,
    Row,
    Col,
    Input
    } from 'reactstrap';

export default class listContact extends Component {
    constructor(){
        super()
        this.state ={
            search:''
        }
    }
    render() {
        let { mode , modal , title , users } = this.props;
        let { search } = this.state;


        return (
            <Modal mode={mode} modal={modal} title={title}>
                <Input type='text' placeholder={'Cari User'} />
                <hr />
                <Scroll style={{ height: '450px' }}>
                            {   
                                users.map((x , i) => (
                                    <Row key={i}>
                                        <Col md='8'>
                                            <ChatItem
                                                avatar={require('assets/img/theme/man.png')}
                                                alt={'Reactjs'}
                                                title={x.USERNAME}
                                                subtitle={x.DIVISION}
                                                statusColor={'#49E20E'}
                                                date={new Date()}
                                                key={i}/>  
                                        </Col>
                                        <Col md='4'>
                                            <Button type='button' size='sm' color='success'>Send Message</Button>
                                        </Col>
                                    </Row>
                                ))
                            }
                        </Scroll>                           
            </Modal>
        )
    }
}
